mapper.update:
delete from sys_role_menu


		and role_id = #{roleId}


		and menu_id = #{menuId}

mapper.select:
select distinct t.id ,t.parent_id parentId , t.name, t.url, t.path, t.css, t.sort, t.create_time createTime , t.update_time updateTime ,t.is_menu isMenu,t.hidden from sys_menu t inner join sys_role_menu r on r.menu_id = t.id where r.role_id in
#{roleId}
ORDER BY sort ASC
